#'       dataLabels = list(enabled = TRUE)
#'     )
#'   ) |>
#'   hc_add_series(
#'     data = df,
#'     type = "column",
#'     hcaes(name = name, y = y),
#'     name = "Things",
#'     colorByPoint = TRUE
#'   )
